    function hashForSignature(address _owner, uint256 _earningsToDate, uint256 _nonce) public pure returns (bytes32) {
        return keccak256(abi.encode(_owner, _earningsToDate, _nonce));
    }




	function validateSig(bytes memory signature,uint a) public returns (address){
        bytes32 eip712DomainHash = keccak256(
            abi.encode(
                keccak256(
                    "EIP712Domain(string name)"
                ),
                keccak256(bytes("Velox"))
            )
        );
        bytes32 hashStruct = keccak256(
            abi.encode(
            // keccak256("Strategy(string a,string b,string c)"),
                keccak256("Strategy(uint256 a)"),
                a
            // b,
            // c
            )
        );
        bytes32 hash = keccak256(abi.encodePacked("\x19\x01", eip712DomainHash, hashStruct));
        address signer = ECDSA.recover(hash, signature);
        return signer;

        return address(this);
	}

}
